// Learn more: https://framer.com/api/property-controls/
addPropertyControls(Acknowledgement, {
    title: {
        title: "Title",
        type: ControlType.String,
        defaultValue: "Witty Title Here",
    },
    text: {
        title: "Text",
        type: ControlType.String,
        displayTextArea: true,
        defaultValue:
            "This is where we heartily acknowledge the wisdom and grace of the user after they've successfully performed a task that we painstakingly oversimplified for them.",
    },
    background: {
        title: "Background",
        type: ControlType.Color,
        defaultValue: "#0099ff",
    },
    color: {
        title: "Content",
        type: ControlType.Color,
        defaultValue: colors.grayDark,
    },
    onTap: {
        type: ControlType.EventHandler,
    },
})
